import numpy as np

def numpy_zero_suppression(values, threshold):
    cond = np.less(np.absolute(values), threshold)
    return np.where(cond, 0, values)

import numba
import numpy as np

@numba.vectorize(nopython=True)
def numba_zero_suppression(values, threshold):
    if np.abs(values) >= threshold:
        return values
    else:
        return 0.0

import numba

@numba.vectorize(['(int16, int16)','(float32, float32)', '(float64, float64)'], target='cuda')
def numba_zero_suppression(values, threshold):
    if abs(values) >= threshold:
        return values
    else:
        return 0.0
